Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wiosłowanie hantlą w oparciu ręką o ławeczkę',CONCAT('1)Pozycja wykroczna przed ławeczką lub innym miejscem podporu.',CHAR(10),'2) Ręka oparta o podwyższenie. ',CHAR(10),'3) Plecy proste, hantla w dłoni, ramię skierowane w dół.'),CONCAT('1) Utrzymuj prawidłową pozycję ciała i wykonaj wdech, a następnie przyciągnij sztangielkę pionowo w górę, kierując ją w stronę biodra, jednoczenie maksymalnie zbliżając łopatkę do kręgosłupa i w dół.',CHAR(10),'2) Kontynuuj ruch do momentu, w którym sztangielka będzie znajdowała się na wysokości biodra, a plecy uzyskają szczytowe napięcie.',CHAR(10),'3) Utrzymaj napięcie przez ułamek sekundy. ',CHAR(10),'4)  opuszczania wykonaj zdecydowanie wolniej niż podnoszenia, zrób wydech.'),CONCAT('1) Postaraj się zachować proste plecy podczas wykonywania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Skoncentruj się na tym, aby ruch następował poprzez napięcie mięśni pleców, a nie ramion.',CHAR(10),'4) Nie szarp ciężaru, ogranicz dodatkowe ruchyciała do minimum.',CHAR(10),'5) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'6) Zbyt szybkie wykonywanie ćwiczenia jest błędem technicznym.',CHAR(10),'7) Jako podparcie możesz wykorzystać dowolną stabilną podporę.'),1,'wioslowanie_w_oparciu_reka_o_laweczke.mp4',3, '/na-plecy/Wioslowanie_hantla_w_oparciu_reka_o_laweczke.jpg');
