Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Podciąganie chwytem młotkowym',CONCAT('1) Drążek trzymany chwytem neutralnym (chwyt młotkowy) na szerokość barków.',CHAR(10),'2) Ramiona wyprostowane, wzrok skierowany przed siebie.',CHAR(10),'3) Łopatki ściągnięte w tył.'),CONCAT('1) Napinając mięsień najszerszy grzbietu i trzymając łopatki złączone, ugnij ramiona w łokciach i prowadząc je wzdłuż tułowia, postaraj się wykonać ruch przyciągania łokci do tułowia.',CHAR(10),'2) Kontynuuj ruch, dopóki broda nie znajdzie się nad drążkiem.',CHAR(10),'3) Dopnij mocno mięśnie pleców, a następnie wykonaj wydech powietrza.',CHAR(10),'4) Wróć do pozycji wyjściowej, opuszczając tułów oraz wykonując płynny wdech.',CHAR(10),'5) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Unikaj kołysania tułowiem i „zarzucania” ciała w górę. Nie odbijaj się także z nóg.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej i poprawną techniką.',CHAR(10),'3) Postaraj się inicjować ruch z mięśni grzbietu, a nie z ramion.',CHAR(10),'4) Skup się podczas wykonywania ruchu.',CHAR(10),'5) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'6) Zwróć uwagę na prawidłową pozycję miednicy. ',CHAR(10),'7) Nie doprowadzaj do pełnego wyprostu ramion podczas opuszczania tułowia.',CHAR(10),'8) Nie śpiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'podciaganie_chwytem_mlotkowym.mp4',3, '/na-plecy/Podciaganie_chwytem_mlotkowym.jpg');
