Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wiosłowanie sztangą trzymaną nachwytem do klatki w opadzie tułowia',CONCAT('1) Trzymając sztangę nachwytem (palce skierowane w dół), nieco szerzej niż na szerokość barków, ugnij lekko nogi w kolanach i wykonaj opad tułowia w przód.',CHAR(10),'2) Ramiona wyprostowane, ustawione prostopadle do podłoża.'),CONCAT('1) Utrzymując prawidłową pozycję ciała, wykonaj wdech, a następnie przyciągnij sztangę pionowo w górę, kieruj ją w stronę mostka, jednocześnie maksymalnie zbliżając łopatki do kręgosłupa.',CHAR(10),'2) Zatrzymaj ruch na ułamek sekundy w momencie, kiedy sztanga znajduje się blisko tułowia.',CHAR(10),'3) Wykonując wydech powietrza, powoli obniż sztangę do pozycji wyjściowej.',CHAR(10),'4) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Postaraj się zachować proste plecy podczas wykonywania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) W czasie wykonywania tego ćwiczenia nie możesz doprowadzać do tworzenia się tzw. kociego grzbietu, czyli wygięcia pleców w łuk.',CHAR(10),'4) Skup się podczas wykonywania ruchu.',CHAR(10),'5) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'6) Postaraj się wykonywać ruch poprzez mocną pracę łopatek.'),1,'wioslowanie_sztanaga_trzymana_nachwytem_do_klatki_w_opadzie_tulowia.mp4',3, '/na-plecy/Wioslowanie_sztanga_trzymana_nachwytem_do_klatki_w_opadzie_tulowia.jpg');
