Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wiosłowanie z wykorzystaniem kółek gimnastycznych, TRX lub sztangi',CONCAT('1) Wykonaj zwis podparty pod sztangą, TRX lub kółkami.',CHAR(10),'2) Sylwetka wyprostowana, mięśnie brzucha oraz pośladków napięte.',CHAR(10),'3) Ramiona wyprostowane, sztanga nieco szerzej niż szerokość barków.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i zacznij uginać ramiona w łokciach, podciągając klatkę piersiową w górę. Podczas wykonywania ruchu łopatki pozostają mocno ściągnięte.',CHAR(10),'2) W najwyższym punkcie zatrzymaj ruch na ułamek sekundy.',CHAR(10),'3) Następnie spokojnym i kontrolowanym ruchem zacznij opuszczać ciało, równocześnie wykonując wydech. ',CHAR(10),'4) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Zachowaj ciało w pełnym wyproście podczas wykonywania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Podczas wykonywania ruchu skup się na pracy łopatek.',CHAR(10),'4) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'5) Postaraj się wykonywać ruch poprzez pracę mięśni grzbietu.'),1,'wioslowanie_z_wykorzystaniem_kolek_gimnastycznych_trx_lub_sztangi.mp4',3, '/na-plecy/Wioslowanie_z_wykorzystaniem_kolek_gimnastycznych,_TRX_lub_sztangi.jpg');
