Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Podciąganie na drążku trzymanym podchwytem',CONCAT('1) Złap za drążek podchwytem (kciuki skierowane na zewnątrz), na szerokość barków',CHAR(10),'2) Ramiona wyprostowane, wzrok skierowany w przód.'),CONCAT('1) Weź wdech, ruch rozpocznij od mocnego ściągnięcia łopatek w dół i do siebie.',CHAR(10),'2) Płynnym ruchem dołącz pracę ramion, łokcie kieruj w stronę bioder. ',CHAR(10),'3) Podciągaj się tak, jakbyś chciał dotknąć drążka klatką piersiową. ',CHAR(10),'4) W najwyższy punkcie wyraźne zatrzymaj ruch, wykonaj wydech i powolnym ruchem rozpocznij opuszczanie aż do pełnego wyprostu ramion.',CHAR(10),'5) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Unikaj kołysania tułowiem i „zarzucania” ciała w górę. Nie odbijaj się także z nóg.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Postaraj się inicjować ruch z mięśni grzbietu, a nie z ramion.',CHAR(10),'4) Podczas wykonywania ruchu skup się na pracy łopatek.',CHAR(10),'5) Zwróć uwagę na prawidłowe oddychanie.'),1,'podciaganie_na_drazku_trzymanym_podchwytem.mp4',3, '/na-plecy/Podciaganie_na_drazku_trzymanym_podchwytem.jpg');
