Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Prostopadłe przyciąganie drążka wyciągu górnego do bioder',CONCAT('1) Pozycja stojąca, przodem do wyciągu. Nogi w lekkim rozkroku, ciało w lekkim opadzie tułowia.',CHAR(10),'2) Gryf prosty trzymany nachwytem na szerokość barków, ramiona wyprostowane w górze.'),CONCAT('1) Weź wdech, a następnie utrzymując proste ramiona, przyciągnij gryf łukiem w kierunku bioder, mocno napinając mięśnie najszersze grzbietu oraz ściągając łopatki do siebie i w dół.',CHAR(10),'2) W momencie gdy dłonie znajdą się przy biodrach, zatrzymaj ruch na ułamek sekundy. Wykonaj wydech.',CHAR(10),'3) Wykonując płynny wdech, wróć do pozycji wyjściowej.',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Unikaj kołysania tułowiem i „rozpędzania” ciężaru.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Postaraj się inicjować ruch pracą mięśni grzbietu.',CHAR(10),'4) Podczas wykonywania ruchu skup się na pracy łopatek.',CHAR(10),'5) Zwróć uwagę na prawidłowe oddychanie.'),1,'prostopadle_przyciaganie_drazka_wyciagu_gornego_do_bioder.mp4',3, '/na-plecy/Prostopadle_przyciaganie_drazka_wyciagu_gornego_do_bioder.jpg');
