Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wiosłowanie końcem sztangi chwytem neutralnym w opadzie tułowia',CONCAT('1) Pozycja stojąca, tułów pochylony pod kątem około 45°, kolana lekko ugięte.',CHAR(10),'2) Ramiona wyprostowane, dłonie trzymają uchwyt typu V, w którym znajduje się sztanga. ',CHAR(10),'3) Sztanga zablokowana pomiędzy ciężarami lub w przeznaczonym w do tego miejscu/uchwycie.'),CONCAT('1) Utrzymując prawidłową pozycję ciała, weź wdech, zainicjuj ruch poprzez ściągnięcie łopatek w dół i do kręgosłupa. Przyciągnij gryf do siebie, unosząc wysoko łokcie aż do momentu, w którym obciążenie dotknie dolnej części klatki piersiowej.',CHAR(10),'2) Wykonaj maksymalny skurcz mięśni grzbietu, zatrzymaj ruch na ułamek sekundy, wykonaj wydech.',CHAR(10),'3) Opuść sztangę do pozycji wyjściowej, wyczuwając mocne rozciągnięcie mięśni pleców.',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Utrzymuj proste plecy podczas wykonywania ćwiczenia.',CHAR(10),'2) Skup się podczas wykonywania ruchu na pracy łopatek.',CHAR(10),'3) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'4) Postaraj się wykonywać ruch poprzez mocną pracę mięśni grzbietu.',CHAR(10),'5) Staraj się nie kołysać tułowiem.',CHAR(10),'6) W celu zwiększenia zakresu ruchu staraj się zakładać na koniec sztangi talerze z mniejszą średnicą.'),1,'wioslowanie_koncem_sztangi_chwytem_neutralnym_w_opadzie_tulowia.mp4',3, '/na-plecy/Wioslowanie_koncem_sztangi_chwytem_neutralnym_w_opadzie_tulowia.jpg');
