Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przyciąganie końca sztangi jednorącz w opadzie tułowia',CONCAT('1) Pozycja stojąca obok sztangi, tułów pochylony pod kątem około 45°, kolana lekko ugięte. ',CHAR(10),'2) Ramię wyprostowane, sztanga trzymana w dłoni. ',CHAR(10),'3) Sztanga zablokowana pomiędzy ciężarami lub w przeznaczonym do tego miejscu/uchwycie.',CHAR(10),'4) Ćwiczenie to można także wykonać, trzymając sztangę między nogami.'),CONCAT('1) Utrzymując prawidłową pozycję, weź wdech, zainicjuj ruch poprzez ściągnięcie łopatki w dół i do kręgosłupa, przyciągnij gryf do siebie w stronę biodra.',CHAR(10),'2) Wykonaj maksymalny skurcz mięśni grzbietu, zatrzymaj ruch na ułamek sekundy, wykonaj wydech.',CHAR(10),'3) Opuść sztangę do pozycji wyjściowej, wyczuwając mocne rozciągnięcie mięśni pleców.',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Utrzymuj proste plecy podczas wykonywania ćwiczenia.',CHAR(10),'2) Podczas wykonywania ruchu skup się na pracy łopatek.',CHAR(10),'3) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'4) Postaraj się wykonywać ruch poprzez mocną pracę mięśni grzbietu.',CHAR(10),'5) Staraj się nie kołysać tułowiem.'),1,'przyciaganie_konca_sztangi_jednoracz_w_opadzie_tulowia.mp4',3, '/na-plecy/Przyciaganie_konca_sztangi_jednoracz_w_opadzie_tulowia.jpg');
