Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przyciąganie linki wyciągu dolnego jednorącz',CONCAT('1) Pozycja siedząca w rozkroku, naprzeciw wyciągu dolnego, z założonym wcześniej pojedynczym uchwytem.',CHAR(10),'2) Chwyt jednorącz za uchwyt. Ramię wyprostowane, wyciągnięte w przód.',CHAR(10),'3) Druga ręka oparta na podłodze lub na kolanie.',CHAR(10),'4) Plecy proste, ustawione niemal prostopadle do podłoża.'),CONCAT('1) Weź wdech i zainicjuj ruch, ściągając łopatkę w dół i do kręgosłupa. Przyciągając uchwyt w stronę biodra, wykonaj lekką rotację w nadgarstku. ',CHAR(10),'2) Ramię prowadź blisko tułowia.',CHAR(10),'3) Zatrzymaj ruch na ułamek sekundy. Następnie powoli wróć do pozycji wyjściowej, wykonując wydech.'),CONCAT('1) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'2) Postaraj się inicjować ruch z mięśni grzbietu, a nie z ramienia.',CHAR(10),'3) Podczas wykonywania ruchu skup się na pracy łopatkami.',CHAR(10),'4) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'5) Prowadź łokieć blisko tułowia. ',CHAR(10),'6) Ćwiczenie można także wykonywać w pozycji stojącej.'),1,'przyciaganie_linki_wyciagu_dolnego_jednoracz.mp4',3, '/na-plecy/Przyciaganie_linki_wyciagu_dolnego_jednoracz.jpg');
