Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Ściąganie drążka wyciągu górnego trzymanego nachwytem za kark',CONCAT('1) Pozycja siedząca, przodem do wyciągu. Kolana zablokowane o wałki.',CHAR(10),'2) Drążek trzymany znacznie szerzej niż szerokość barków. Ramiona wyprostowane w górze.'),CONCAT('1) Weź wdech, pochyl się nieznacznie do przodu, zainicjuj ruch poprzez ściągnięcie łopatek w dół i do siebie, przyciągnij drążek w okolice karku, opuszczając łokcie w linii tułowia w kierunku bioder.',CHAR(10),'2) W momencie największego napięcia zatrzymaj ruch na ułamek sekundy.',CHAR(10),'3) Rozpocznij opuszczanie ciężaru wraz z płynnym wydechem.'),CONCAT('1) Unikaj kołysania tułowiem i „rozpędzania” drążka ciałem.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Postaraj się inicjować ruch z mięśni grzbietu, a nie z ramion.',CHAR(10),'4) Podczas wykonywania ruchu skup się na pracy łopatek.',CHAR(10),'5) Zwróć uwagę na prawidłowe oddychanie.'),2,'sciaganie_drazka_wyciagu_gornego_nachwytem_za_kark.mp4',3, '/na-plecy/Sciaganie_drazka_wyciagu_gornego_trzymanego_nachwytem_za_kark.jpg');
