Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Unoszenie tułowia na ławce rzymskiej',CONCAT('1) Leżenie na ławeczce rzymskiej, biodra na oparciach ławeczki.',CHAR(10),'2) Ciało w linii prostej, ramiona skrzyżowane na klatce piersiowej.'),CONCAT('1) Utrzymując prawidłową pozycję, wykonaj wdech i opuść ciało tak, aby cały czas zachować plecy proste, jedyny ruch odbywa się w stawach biodrowych. ',CHAR(10),'2) Następnie wykonaj płynny powrót ciała do pozycji początkowej połączony z wydechem w końcowej fazie ruchu. ',CHAR(10),'3) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Postaraj się zachować proste plecy podczas wykonywania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli, z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Oś zgięcia przechodzi przez staw biodrowy.',CHAR(10),'4) Skup się podczas wykonywania ruchu.'),2,'unoszenie_tulowia_na_lawce_rzymskiej.mp4',3, '/na-plecy/Unoszenie_tulowia_na_lawce_rzymskiej.jpg');
