Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wiosłowanie hantlami w opadzie tułowia',CONCAT('1) Pozycja stojąca z opadem tułowia około 45°, hantle trzymane chwytem młotkowym.',CHAR(10),'2) Ramiona wyprostowane, ustawione prostopadle do podłoża.'),CONCAT('1) Utrzymując prawidłową pozycję, wykonaj wdech, a następnie przyciągnij hantle pionowo w górę, kierując je w stronę bioder, jednocześnie maksymalnie zbliżając łopatki do kręgosłupa.',CHAR(10),'2) Zatrzymaj ruch na ułamek sekundy w momencie, kiedy hantle znajdują się blisko bioder.',CHAR(10),'3) Wykonując wydech powietrza, powoli obniż sztangę do pozycji wyjściowej.',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Postaraj się zachować plecy proste podczas wykonywania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) W czasie wykonywania tego ćwiczenia nie możesz doprowadzać do tworzenia się tzw. kociego grzbietu, czyli wygięcia pleców w łuk.',CHAR(10),'4) Skup się podczas wykonywania ruchu.',CHAR(10),'5) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'6) Postaraj się wykonywać ruch poprzez mocną pracę łopatek.'),2,'wioslowanie_hantlami_w_opadzie_tulowia.mp4',3, '/na-plecy/Wioslowanie_hantlami_w_opadzie_tulowia.jpg');
