Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wiosłowanie sztangą trzymaną podchwytem w opadzie tułowia',CONCAT('1) Trzymając sztangę podchwytem (kciuki skierowane na zewnątrz), nieco szerzej niż na szerokość barków, ugnij lekko nogi w kolanach i wykonaj opad tułowia w przód.',CHAR(10),'2) Ramiona wyprostowane, ustawione prostopadle do podłoża.'),CONCAT('1) Utrzymując prawidłową pozycję, wykonaj wdech, a następnie przyciągnij sztangę pionowo w górę, kierując ją w stronę mostka, jednocześnie maksymalnie zbliżając łopatki do kręgosłupa.',CHAR(10),'2) Zatrzymaj ruch na ułamek sekundy w momencie, kiedy sztanga znajdzie się blisko tułowia.',CHAR(10),'3) Wykonując wydech, powoli obniż sztangę do pozycji wyjściowej.',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Utrzymuj plecy proste podczas wykonywania ćwiczenia.',CHAR(10),'2) Ćwiczenie wykonuj powoli z pełnym wyczuciem zaangażowanej grupy mięśniowej.',CHAR(10),'3) Podczas wykonywania ruchu skup się na pracy łopatek.',CHAR(10),'4) Zwróć uwagę na prawidłowe oddychanie.',CHAR(10),'5) Postaraj się wykonywać ruch poprzez mocną pracę łopatek.'),2,'wioslowanie_sztanga_trzymana_podchwytem_w_opadzie_tulowia.mp4',3, '/na-plecy/Wioslowanie_sztanga_trzymana_podchwytem_w_opadzie_tulowia.jpg');
