Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Uginanie ramion z gumą',CONCAT('1) Pozycja stojąca, ustabilizowana sylwetka, mięśnie brzucha i pośladki mocno napięte.',CHAR(10),'2) Ustabilizowane łokcie, które znajdują się możliwie jak najbliżej tułowia.',CHAR(10),'3) Guma oporowa trzymana podchwytem, zablokowana pod stopami.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i wykonaj dokładne płynne zgięcie przedramion w pełnym zakresie ruchu.',CHAR(10),'2) Utrzymaj maksymalnie napięty biceps przez ułamek sekundy.',CHAR(10),'3) Następnie powolnym ruchem opuść przedramiona do pełnego rozciągnięcia, wykonaj również płynny wydech.'),CONCAT('1) Nie bujaj ciałem.',CHAR(10),'2) Nie odrywaj łokci.',CHAR(10),'3) Trzymaj głowę w równej linii z plecami.',CHAR(10),'4) Tylko przedramiona są ruchome.'),1,'video-uginanie-ramion-z-guma.mp4',6, '/na-biceps/Uginanie_ramion_z_guma.jpg');
