Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Zginanie ramion ze sztangą nachwytem',CONCAT('1) Pozycja stojąca, ciało wyprostowane, sztanga trzymana nachwytem na szerokość braków.',CHAR(10),'2) Stopy na szerokość barków, pośladki i brzuch mocno spięte.',CHAR(10),'3) Pełne ustabilizowanie łokci, które znajdują się możliwie blisko tułowia.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i przytrzymując ramiona nieruchomo, wykonaj ugięcie przedramion ze sztangą do momentu szczytowego napięcia bicepsa, w którym sztanga powinna znajdować w okolicach wysokości barków. ',CHAR(10),'2) Utrzymaj maksymalnie napięty biceps przez ułamek sekundy.',CHAR(10),'3) Następnie powolnym ruchem rozpocznij opuszczanie, jednocześnie wypuszczając powietrze.'),CONCAT('1) Każde powtórzenie wykonuj z pełnym rozciągnięciem mięśni. ',CHAR(10),'2) Zadbaj o zachowanie prostej postawy.',CHAR(10),'3) Napnij mięśnie brzucha i pośladki w celu dodatkowej stabilizacji odcinka lędźwiowego kręgosłupa.',CHAR(10),'4) Nie szarp ciężaru. Zarówno łokcie, jak i ramiona są unieruchomione.',CHAR(10),'5) Tylko przedramiona są ruchome.',CHAR(10),'6) Unikaj zginania pleców i bujania tułowiem.',CHAR(10),'7) Bądź skoncentrowany.'),1,'zginanie_ramion_ze_sztanga_nachwytem.mp4',6, '/na-biceps/Zginanie_ramion_ze_sztanga_nachwytem.jpg');
