Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Zginanie ramion z hantlami na modlitewniku chwytem młotkowym',CONCAT('1) Pozycja siedząca na modlitewniku, łokcie wsparte na pulpicie, plecy wyprostowane.',CHAR(10),'2) Klatka piersiowa przylega do modlitewnika, łopatki ściągnięte. ',CHAR(10),'3) Ramiona ugięte w łokciach, hantle blisko przy brodzie, trzymane chwytem młotkowym. ',CHAR(10),'4) Nogi rozstawione w sposób pozwalający zachować stabilną pozycję.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech, dokładnie, kontrolowanym ruchem opuść przedramię z ciężarem.',CHAR(10),'2) Ramię opuszczaj do pełnego wyprostu, lecz nie pozwól na przeprost ramienia w łokciu. ',CHAR(10),'3) Utrzymując powietrze, rozpocznij uginanie, w końcowej fazie wypuść powietrze. ',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Nie doprowadzaj do przeprostu w łokciach. ',CHAR(10),'2) Nie szarp ciężaru. Nie odrywaj łokci od pulpitu. Pamiętaj o spokojnym odłożeniu ciężaru.',CHAR(10),'3) Trzymaj głowę w równej linii z plecami.',CHAR(10),'4) Tylko przedramiona są ruchome.',CHAR(10),'5) Plecy są wyprostowane.',CHAR(10),'6) Bądź skoncentrowany.'),1,'zginanie_ramion_z_hantlami_na_modlitewniku_chwytem_mlotkowym.mp4',6, '/na-biceps/Zginanie_ramion_z_hantlami_na_modlitewniku_chwytem_mlotkowym.jpg');
