Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Uginanie ramienia z linką wyciągu dolnego stojąc',CONCAT('1) Pozycja stojąca, tyłem do wyciągu dolnego, około pół metra od wyciągu.',CHAR(10),'2) Brzuch i pośladki mocno napięte, ciało wyprostowane. ',CHAR(10),'3) Uchwyt linki wyciągu dolnego trzymany w jednej dłoni, ramię lekko cofnięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i wykonaj dokładne, płynne zgięcie ramienia w pełnym zakresie ruchu aż do momentu, w którym przedramię dotknie bicepsa, a łokieć lekko się uniesie. ',CHAR(10),'2) Przytrzymaj maksymalnie napięty biceps przez ułamek sekundy.',CHAR(10),'3) Następnie wraz z wydechem powolnym ruchem opuść przedramię.'),CONCAT('1) Nie szarp ciężaru. ',CHAR(10),'2) Trzymaj głowę w równej linii z plecami.',CHAR(10),'3) Plecy są wyprostowane.',CHAR(10),'4) Bądź skoncentrowany.'),1,'uginanie_ramienia_z_linka_dolnego_wyciagu_stojac.mp4',6, '/na-biceps/Uginanie_ramienia_z_linka_wyciagu_dolnego_stojac.jpg');
