Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Uginanie ramion z hantlami z rotacją',CONCAT('1) Pozycja stojąca. ',CHAR(10),'2) Ramiona ułożone wzdłuż ciała, brzuch i pośladki napięte.',CHAR(10),'3) Hantle w dłoniach w pozycji neutralnej – kciuki skierowane w przód.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech.',CHAR(10),'2) Rozpocznij uginanie z jednoczesną rotacją zewnętrzną (supinacją), aby finalnie dłoń była skierowana kciukiem na zewnątrz.',CHAR(10),'3) Zatrzymaj ruch na ułamek sekundy w pozycji końcowej. ',CHAR(10),'4) Z wydechem wykonaj ruch opuszczania znacznie wolniejniż podnoszenia, wracając do neutralnej pozycji ramion.'),CONCAT('1) Nie szarp ciężaru. Nie odrywaj łokci. Nie bujaj ciałem.',CHAR(10),'2) Pamiętaj o kontrolowanym opuszczaniu ciężaru.',CHAR(10),'3) Dobierz ciężar tak, aby każde powtórzenie było wykonane zgodnie z prawidłową techniką.',CHAR(10),'4) Bądź skoncentrowany. Utrzymuj stałe napięcie mięśni brzucha i pośladków.'),1,'uginanie_ramion_z_hantlami_z_rotacja.mp4',6, '/na-biceps/Uginanie_ramion_z_hantlami_z_rotacja.jpg');
