Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Zginanie przedramienia z hantlem – łokieć oparty na udzie',CONCAT('1) Pozycja siedząca na ławeczce, nogi ustawione szeroko.',CHAR(10),'2) Tułów w opadzie, ramię ćwiczące oparte łokciem o wewnętrzną część uda.',CHAR(10),'3) Ramie niećwiczące podparte na kolanie przeciwnej nogi.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech i mocne zgięcie ramienia w łokciu w pełnym zakresie ruchu, zachowując stałe napięcie mięśni.',CHAR(10),'2) Przytrzymaj maksymalnie napięty biceps przez ułamek sekundy w szczytowym momencie ruchu, następnie powolnym ruchem opuść przedramię. ',CHAR(10),'3) W końcowej fazie ruchu wykonaj wydech. ',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Tempo: 1 sekunda w górę, 2 sekundy w dół.',CHAR(10),'2) Stopy mocno wciśnięte w podłogę.',CHAR(10),'3) Unikaj dynamicznego rozpędzania hantla i „wahadła”.',CHAR(10),'4) Nie szarp ciężaru. Pamiętaj o spokojnym odłożeniu ciężaru.',CHAR(10),'5) Tylko przedramiona są ruchome.',CHAR(10),'6) Bądź skoncentrowany.'),2,'zginanie_przedramienia_z_hantlem_lokiec_oparty_na_udzie.mp4',6, '/na-biceps/Zginanie_przedramienia_z_hantlem_–_lokiec_oparty_na_udzie.jpg');
