Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Zginanie przedramion z hantlami z rotacją nadgarstka siedząc na ławce 90 stopni',CONCAT('1) Siedząc na ławce z oparciem 90 stopni, opuść swobodnie ramiona tak, aby były prostopadłe do podłoża.',CHAR(10),'2) Łokcie znajdują się możliwie jak najbliżej tułowia.',CHAR(10),'3) Sztangielki trzymane chwytem młotkowym (chwyt neutralny).'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i przytrzymując ramiona nieruchomo, wykonaj wdech oraz ugięcie ramion w łokciu wraz z półobrotem nadgarstka na zewnątrz. ',CHAR(10),'2) Utrzymaj maksymalnie napięty biceps przez ułamek sekundy, w szczytowym momencie ruchu podnieś łokieć w celu dopięcia mięśnia dwugłowego. Wykonaj wydech, a następnie powolnym ruchem opuść przedramiona.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Tempo: 1 sekunda w górę, 2 sekundy w dół.',CHAR(10),'2) Stopy mocno zaparte o podłogę.',CHAR(10),'3) Nie szarp ciężaru. Nie odrywaj łokci. Pamiętaj o spokojnym odłożeniu ciężaru.',CHAR(10),'4) Trzymaj głowę w równej linii z plecami.',CHAR(10),'5) Bądź skoncentrowany.'),2,'zginanie_przedramion_z_hantlami_z_rotacja_nadgarstka_siedzac_na_lawce_90_stopni.mp4',6, '/na-biceps/Zginanie_przedramion_z_hantlami_z_rotacja_nadgarstka_siedzac_na_lawce_90_stopni.jpg');
