Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Zginanie przedramion ze sztangą stojąc',CONCAT('1) Pozycja stojąca, plecy proste, sztanga trzymana podchwytem.',CHAR(10),'2) Pełne ustabilizowanie łokci, które znajdują się możliwie jak najbliżej tułowia.',CHAR(10),'3) Dłonie rozstawione na szerokość nieco większą niż rozstaw barków.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i przytrzymując ramiona nieruchomo, wykonaj ugięcie ramion w łokciach ze sztangą do momentu szczytowego napięcia bicepsa, w którym sztanga powinna znajdować się na wysokości barków. Utrzymuj stałe napięcie mięśni.',CHAR(10),'2) Przytrzymaj maksymalnie napięty biceps przez ułamek sekundy, w szczytowym momencie ruchu wykonaj wydech, a następnie powolnym ruchem opuść przedramiona.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Tempo: 1 sekunda w górę, 2 sekundy w dół.',CHAR(10),'2) Stopy mocno zaparte opodłogę. Zadbaj o zachowanie prostej postawy ciała.',CHAR(10),'3) Napnij mięśnie brzucha i pośladków w celu dodatkowej stabilizacji odcinka lędźwiowego kręgosłupa.',CHAR(10),'4) Nie szarp ciężaru. Nie odrywaj łokci. Pamiętaj o spokojnym odłożeniu ciężaru.',CHAR(10),'5) Tylko przedramiona są ruchome.',CHAR(10),'6) Bądź skoncentrowany.'),2,'zginanie_przedramion_ze_sztanga_stojac.mp4',6, '/na-biceps/Zginanie_przedramion_ze_sztanga_stojac.jpg');
