Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wspięcia na palce jednonóż z hantlami',CONCAT('1) Pozycja stojąca, plecy wyprostowane, ramię wzdłuż tułowia, sztangielka w dłoni.',CHAR(10),'2) Stopy ustawione na podwyższeniu, wolna ręka utrzymuje równowagę.'),CONCAT('1) W pozycji stojącej na podwyższeniu unieś jedną nogę w górę. ',CHAR(10),'2) Pięta nogi, na której stoisz, powinna być mocno obniżona, a mięśnie łydki maksymalnie rozciągnięte. ',CHAR(10),'3) Energicznym ruchem wykonaj wspięcie na palce i zatrzymaj ruch w maksymalnym punkcie. ',CHAR(10),'4) Następnie powolnym, kontrolowanym ruchem opuść piętę aż do maksymalnego rozciągnięcia.'),CONCAT('1) Jako podwyższenie możesz użyć ciężaru. ',CHAR(10),'2) Przednia część podeszwy stopy stabilnie ustawiona na podwyższeniu.',CHAR(10),'3) Przy każdym powtórzeniu należy wykonać pełne zgięcie stopy, aby w pełni rozciągnąć mięśnie.',CHAR(10),'4) Bądź skoncentrowany.'),1,'wspiecia_na_palce_jednonoz_z_hantlami.mp4',5, '/na-lydki/Wspiecia_na_palce_jednonoz_z_hantlami.jpg');
