Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Rozpiętki na TRX',CONCAT('1) Podpór przodem na taśmach TRX.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała. Dłonie w jednej linii, ustawione równolegle do siebie nieco szerzej niż rozstaw barków.',CHAR(10),'3) Nogi wyprostowane, ciało w jednej linii.',CHAR(10),'4) Stopy mocno zaparte o podłoże.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj mocny wdech do brzucha, następnie przez odwodzenie ramion w bok opuszczaj ciało. Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały.',CHAR(10),'2) W pozycji dolnej powinieneś czuć rozciąganie klatki piersiowej.',CHAR(10),'3) Następnie płynnym kontrolowanym ruchem wraz z wydechem wróć do pozycji wyjściowej przez przywodzenie ramion do środka.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia.',CHAR(10),'2) Stopy solidnie zaprzyj o podłogę.',CHAR(10),'3) Podczas ćwiczenia utrzymuj łokcie w minimalnym ugięciu, lecz mocno zablokowane.',CHAR(10),'4) W trakcie napinania mięśni brzucha i mięśni pośladkowych zachowuj prawidłową (wyprostowaną) postawę ciała.',CHAR(10),'5) Upewnij się, że taśmy są odpowiednio zamocowane.',CHAR(10),'6) Zbyt szybkie wykonywanie ćwiczenia jest błędem technicznym.'),1,'rozpietki_trx.mp4',1, '/na-klatke-piersiowa/Rozpietki_na_TRX.jpg');
