Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki podwieszane na TRX',CONCAT('1) Podpór przodem. Nogi podwieszone w uchwytach taśm TRX.',CHAR(10),'2) Tułów wyprostowany, ręce ustawione prostopadle do osi ciała. Dłonie w jednej linii, ustawione równolegle do siebie na szerokości barków.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj płynny wdech. Zacznij zginać przedramiona i opuszczać klatkę piersiową do podłoża. Należy zadbać o to, aby tułów był cały czas w linii prostej, a biodra nie opadały.',CHAR(10),'2) Wróć do pozycji wyjściowej, prostując ramiona. Kończąc ruch, wykonaj wydech.',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Kontroluj ruch podczas wykonywania ćwiczenia.',CHAR(10),'2) Bądź skoncentrowany.',CHAR(10),'3) W trakcie napinania mięśni brzucha oraz mięśni pośladkowych zachowuj prawidłową (wyprostowaną) postawę ciała.',CHAR(10),'4) Pamiętaj o prawidłowym oddychaniu.',CHAR(10),'5) Łokcie prowadź na zewnątrz, nie powinny dotykać tułowia.'),1,'pompki_podwieszone_trx.mp4',1, '/na-klatke-piersiowa/Pompki_podwieszane_na_TRX.jpg');
