Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie sztangi na ławce skośnej głową w dół',CONCAT('1) Połóż się na ławce ze skosem ujemnym.',CHAR(10),'2) Złap sztangę nieco szerzej niż na szerokość barków. ',CHAR(10),'3) Unieś sztangę ze stojaków, utrzymuj łopatki mocno ściśnięte ze sobą.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech i powolnym ruchem opuść sztangę w stronę dolnej części klatki piersiowej.',CHAR(10),'2) Po przytrzymaniu sztangi przez ułamek sekundy rozpocznij wyciskanie, w końcowej fazie wykonując wydech powietrza.',CHAR(10),'3) W momencie wyprostowania ramion ze sztangą (unikaj przeprostu w łokciach) mocno dopnij mięsień piersiowy, po czym ponownie zacznij opuszczać ramiona.',CHAR(10),'4) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Tempo opuszczania sztangi powinno być dwa razy wolniejsze niż tempo wyciskania.',CHAR(10),'2) Utrzymuj stałe napięcie mięśni.',CHAR(10),'3) Postaraj się nie wyginać nadmiernie nadgarstków.',CHAR(10),'4) Dbaj o staw łokciowy, nie doprowadzaj doprzeprostu ramienia pod ciężarem.',CHAR(10),'5) Skup się na prawidłowym oddychaniu.',CHAR(10),'6) Bądź skoncentrowany.',CHAR(10),'7) Jeżeli zamierzasz wyciskać duże ciężary, poproś o pomoc i asekurację.'),1,'wyciskanie_sztangi_lawce_skosnej_glowa_w_dol.mp4',1, '/na-klatke-piersiowa/Wyciskanie_sztangi_na_lawce_skosnej_glowa_w_dol.jpg');
