Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Floor press',CONCAT('1) Połóż się na podłodze, ugnij nogi.',CHAR(10),'2) Unieś sztangielki. Ramiona ustaw pod kątem około 45 stopni względem ciała.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech do brzucha.',CHAR(10),'2) Zdecydowanym, kontrolowanym ruchem wyciśnij ciężar w górę poprzez wyprost ramion, w końcowej fazie ruchu wykonaj wydech.',CHAR(10),'3) W momencie prostowania ramion ze sztangielkami unikaj przeprostu w łokciach.',CHAR(10),'4) Spokojnym ruchem wróć do pozycji wyjściowej, wykonując wdech.'),CONCAT('1) Pamiętaj, że tempo opuszczania sztangielekpowinno być dwa razy wolniejsze niż tempo wyciskania.',CHAR(10),'2) Utrzymuj stałe napięcie mięśni.',CHAR(10),'3) Postaraj się nie wyginać nadmiernie nadgarstków.',CHAR(10),'4) Dbaj o staw łokciowy, nie doprowadzaj do pełnego wyprostu ramienia pod ciężarem.',CHAR(10),'5) Skup się na prawidłowym oddychaniu przeponowym.',CHAR(10),'6) Bądź skoncentrowany.',CHAR(10),'7) Zbyt szybkie wykonywanie ćwiczenia jest błędem technicznym.'),1,'floor_press_1.mp4',1, '/na-klatke-piersiowa/Floor_press.jpg');
