Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przenoszenie sztangielki za głowę',CONCAT('1) Pozycja leżąca na ławeczce płaskiej.',CHAR(10),'2) Stopy w lekkim rozkroku mocno zaparte o podłoże.',CHAR(10),'3) Ramiona uniesione prostopadle do podłogi.',CHAR(10),'4) Sztangielka trzymana oburącz, krążek spoczywa na wewnętrznej części dłoni, kciuk i palec wskazujący obejmują uchwyt.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech i powolnym, półkolistym ruchem opuść sztangielkę za głowę, aż poczujesz silne rozciąganie w klatce piersiowej. Łokcie utrzymuj lekko zgięte.',CHAR(10),'2) Następnie rozpocznij powrót do pozycji wyjściowej, przenosząc sztangielkę na wysokość mostka oraz wykonując wydech. ',CHAR(10),'3) Wykonaj ruch zadaną liczbę powtórzeń.'),CONCAT('1) Mięśnie brzucha mocno napięte.',CHAR(10),'2) Utrzymuj stałe napięcie mięśni klatki piersiowej.',CHAR(10),'3) Skup się na prawidłowym oddychaniu.',CHAR(10),'4) Zbyt szybkie wykonywanie ćwiczenia jest błędem technicznym.'),1,'przenoszenie_sztangielki_za_glowe.mp4',1, '/na-klatke-piersiowa/Przenoszenie_sztangielki_za_glowe.jpg');
