Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki (wersja klasyczna)',CONCAT('1) Przyjmij pozycję podporu przodem. ',CHAR(10),'2) Dłonie ustaw na wysokości barków, nieco szerzej niż ich szerokość, palce dłoni skieruj do przodu. Zablokuj ramiona w stawach łokciowych.',CHAR(10),'3) Zachowaj naturalną krzywiznę kręgosłupa.',CHAR(10),'4) Napnij mięśnie brzucha i pośladki.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj dokładne, płynne i powolne opuszczenie klatki piersiowej (bez dotykania podłoża) połączone z wdechem powietrza, a następnie dynamiczne podnieś tułów wraz z wydechem powietrza.  ',CHAR(10),'2) Wykonaj zadaną liczbę powtórzeń.'),CONCAT('1) Pamiętaj o utrzymywaniu napięcia mięśni brzucha i pośladków podczas całego ruchu. ',CHAR(10),'2) Cały czas kontroluj ruch opuszczania tułowia. ',CHAR(10),'3) Dynamicznie i mocno podnoś tułów. ',CHAR(10),'4) Utrzymuj głowę w równej linii z kręgosłupem (wzrok skierowany na podłoże). ',CHAR(10),'5) Bądź skoncentrowany.'),1,'pompki_tradycyjne_pompka_klasyczna.mp4',1, '/na-klatke-piersiowa/Pompki_(wersja_klasyczna).jpg');
