Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Pompki na poręczach',CONCAT('1) Chwytając za poręcze, unieś ciało w górę poprzez mocne wyprostowanie ramion. ',CHAR(10),'2) Tułów utrzymuj prostopadle do podłoża.'),CONCAT('1) Pochyl tułów do przodu do momentu, w którym poczujesz rozciąganie w klatce piersiowej. Tak pochylony wykonaj wdech i zginając ramiona w łokciach, zejdź do momentu, w którym klatka będzie znajdowała się w okolicy poręczy. ',CHAR(10),'2) Wykonując wydech, prostuj ramiona, powracając do pozycji wyjściowej. ',CHAR(10),'3) Powtórz ruch zalecaną ilość powtórzeń.'),CONCAT('1) Kontroluj ruch podczas trwania ćwiczenia. ',CHAR(10),'2) Bądź skoncentrowany. ',CHAR(10),'3) W celu uniknięcia urazu wykonując to ćwiczenie po raz pierwszy, nie należy opuszczać ciała przesadnie nisko.'),1,'pompki_na_poreczach.mp4',1, '/na-klatke-piersiowa/Pompki_na_poreczach.jpg');
