Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Zercher squat',CONCAT('1) Pozycja stojąca.',CHAR(10),'2) Sztanga ułożona przed klatką piersiową, trzymana w zgiętych ramionach.',CHAR(10),'3) Stopy ustawione mniej więcej na szerokość barków (palce stóp na zewnątrz).',CHAR(10),'4) Klatka piersiowa wypchnięta, łopatki ściągnięte, brzuch i pośladki napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech, wykonaj płynny i powolny przysiad, tak bybiodra znalazły się poniżej linii kolan.',CHAR(10),'2) Następnie wróć do pozycji wyjściowej poprzez wyprost w stawach biodrowych i kolanowych. Nie wykonuj przeprostu w stawach kolanowych.',CHAR(10),'3) W końcowej fazie ruchu wykonaj wydech.'),CONCAT('1) Kontroluj ruch siadu.',CHAR(10),'2) Nie bujaj tułowiem (trzymaj tułów stabilnie w jednej linii).',CHAR(10),'3) Głowę trzymaj w równej linii z plecami.',CHAR(10),'4) Wzrok skieruj przed siebie.',CHAR(10),'5) Nie wykonuj kociego grzbietu.',CHAR(10),'6) Nie wykonuj przeprostu w kolanach.',CHAR(10),'7) Bądź skoncentrowany.',CHAR(10),'8) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz).',CHAR(10),'9) Ciężar rozłóż równomiernie na całych stopach.'),1,'video-zercher-squat.mp4',4, '/czworoglowe-uda/Zercher_squat.jpg');
