Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Landmine reverse lunges',CONCAT('1) Pozycja stojąca, plecy proste. ',CHAR(10),'2) Koniec sztangi trzymany oburącz przed klatką piersiową. ',CHAR(10),'3) Stopy ustawione mniej więcej na szerokość barków (palce stóp lekko na zewnątrz). ',CHAR(10),'4) Klatka piersiowa wypchnięta, łopatki ściągnięte, brzuch i pośladki napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i wykonaj zakrok.  ',CHAR(10),'2) Następnie ugnij kolana tak, aby kolano nogi zakrocznej znajdowało się tuż nad podłogą.  ',CHAR(10),'3) Następnie wydychając powietrze, wróć do pozycji początkowej.'),CONCAT('1) Pamiętaj o utrzymaniu prostych pleców. ',CHAR(10),'2) Nie bujaj tułowiem (trzymaj stabilnie w jednej linii). ',CHAR(10),'3)Głowa powinna stanowić przedłużenie kręgosłupa. ',CHAR(10),'4) Wzrok skieruj przed siebie. ',CHAR(10),'5) Nie wykonuj przeprostu w kolanach. ',CHAR(10),'6) Nie prowadź kolan do wewnątrz.'),1,'video-landmine-reverse-lunges.mp4',4, '/czworoglowe-uda/Landmine_reverse_lunges.jpg');
