Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Reverse drop lunges with dumbbell',CONCAT('1) Pozycja stojąca na podwyższeniu.',CHAR(10),'2) Hantelki trzymane chwytem młotkowym.',CHAR(10),'3) Łopatki ściągnięte, brzuch i pośladki napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i schodząc z podwyższenia, wykonaj zakrok jedną nogą do tyłu. Noga wykroczna pozostaje w miejscu.',CHAR(10),'2) W pozycji wykroczno-zakrocznej ugnij kolana tak, aby kolano nogi zakrocznej znalazło się tuż nad podłogą. Nie pochylaj się.',CHAR(10),'3) Wykonując wydech, przenieś ciężar na nogę wykroczną i poprzez energiczną pracę nóg wróć do pozycji wyjściowej.'),CONCAT('1) Kontroluj ruch zakroku.',CHAR(10),'2) Nie bujaj tułowiem, utrzymuj kolce biodrowe na jednej wysokości.',CHAR(10),'3) Głowę trzymaj w równej linii z plecami.',CHAR(10),'4) Wzrok skieruj przed siebie.',CHAR(10),'5) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz).',CHAR(10),'6) Nie spiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'video-reverse-drop-lunges-with-dumbbell.mp4',4, '/czworoglowe-uda/Reverse_drop_lunges_with_dumbbell.jpg');
