Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przysiad z wyskokiem na TRX',CONCAT('1) Proste plecy i odcinek lędźwiowy.',CHAR(10),'2) Taśmy TRX utrzymuj na wysokości klatki piersiowej, ramiona lekko ugięte.',CHAR(10),'3)Obniżone żebra, napięte mięśnie brzucha oraz pośladki.',CHAR(10),'4) Stopy mniej więcej na szerokości barków.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i wykonaj przysiad do pozycji poniżej kąta prostego, cały czas utrzymuj lekko napięte taśmy TRX.',CHAR(10),'2) Z mocnym wydechem wykonaj dynamiczny wyprost kolan wraz z wyskokiem.',CHAR(10),'3) Przy opadaniu amortyzuj skok, uginając kolana. Schodząc do pozycji przysiadu, przygotuj się już do kolejnego skoku.'),CONCAT('1) Pamiętaj o utrzymaniu prostych pleców.',CHAR(10),'2) Wykonaj dynamiczny wyskok.',CHAR(10),'3) Bądź skoncentrowany.',CHAR(10),'4) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz).',CHAR(10),'5) Ciężar rozłóż równomiernie na całych stopach.'),1,'przysiad_z_wyskokiem_trx.mp4',4, '/czworoglowe-uda/Przysiad_z_wyskokiem_na_TRX.jpg');
