Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Kozak squat bez ciężaru',CONCAT('1)Pozycja rozkroczna, szerzej niż szerokość barków.'),CONCAT('1) Z pozycji początkowej wykonaj mocny wdech do przepony. Następnie rozpocznij przysiad, przenosząc ciężar ciałana jedną nogę. ',CHAR(10),'2)  wykonuj do momentu wykonania pełnego przysiadu na nodze postawnej lub do momentu bardzo mocnego rozciągania drugiej nogi.',CHAR(10),'3) Następnie poprzez wyprost w kolanie i biodrze wróć do pozycji początkowej.'),CONCAT('1) Postaraj się jak najmniej pochylać tułów.',CHAR(10),'2) Podczas wykonywania przysiadu utrzymuj stopę na podłodze.',CHAR(10),'3)Podczas wykonywania przysiadu pilnuj, aby kolano nie było skierowane do środka, utrzymuj nogę w lekkiej rotacji na zewnątrz. ',CHAR(10),'4) Skup się podczas wykonywania ruchu.',CHAR(10),'5) Napinaj mocno mięśnie brzucha podczas wykonywania całego ruchu.',CHAR(10),'6) Zbyt szybkie wykonywanie ruchu jest błędem technicznym.'),1,'kozak_squat_bez_ciezaru.mp4',4, '/czworoglowe-uda/Kozak_squat_bez_ciezaru.jpg');
