Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przysiad z TRX',CONCAT('1) Pozycja stojąca.',CHAR(10),'2) Uchwyty taśm trzymane w rękach, lekkie odchylenie tułowia.'),CONCAT('1)Z pozycji początkowej wykonaj mocny wdech do przepony, a następnie spokojnym, kontrolowanym ruchem rozpocznij wykonywanie przysiadu poprzez jednoczesne zgięcie w stawach kolanowych oraz biodrowych.',CHAR(10),'2) W trakcie ruchu kolana skieruj na zewnątrz, ramiona lekko spięte pomagają utrzymać równowagę.',CHAR(10),'3) Przysiad wykonuj do momentu, gdy biodra znajdą się poniżej kolan.',CHAR(10),'4) Poprzez mocny wyprost w stawach kolanowych i biodrowych wróć do pozycji początkowej, wykonując równocześnie wydech.'),CONCAT('1) Pamiętaj o utrzymywaniu prawidłowej pozycji kręgosłupa.',CHAR(10),'2) Kontroluj ruch przysiadu.',CHAR(10),'3) Nie bujaj tułowiem (trzymaj go stabilnie w jednej linii).',CHAR(10),'4) Głowę trzymaj w równej linii z plecami.',CHAR(10),'5) Nie wykonuj kociego grzbietu.',CHAR(10),'6) Nie wykonuj przeprostu w kolanach.',CHAR(10),'7) Bądź skoncentrowany.',CHAR(10),'8) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz).',CHAR(10),'9) Ciężar rozłóż równomiernie na całych stopach.',CHAR(10),'10) Staraj się jak najmniej pracować ramionami, niech taśmy służą głównie jako pomoc w utrzymaniu równowagi.'),1,'trx_przysiad2.mp4',4, '/czworoglowe-uda/Przysiad_z_TRX.jpg');
