Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przysiad wykroczny z nogą zakroczną na podwyższeniu z hantlami',CONCAT('1) Pozycja wykroczna ze stopą na podwyższeniu, nogi wyprostowane.',CHAR(10),'2) Plecy wyprostowane, łopatki ściśnięte ze sobą.',CHAR(10),'3) Ramiona z hantlami spoczywają swobodnie wzdłuż tułowia. ',CHAR(10),'4) Mięśnie brzucha napięte.'),CONCAT('1) Wykonaj głęboki wdech do przepony, ugnij kolana tak, aby utrzymać ciało w jednej linii.',CHAR(10),'2)  kontynuuj do momentu, gdy kolano nogi zakrocznej znajdzie się tuż nad podłogą lub gdy poczujesz bardzo mocne rozciąganie nogi zakrocznej.',CHAR(10),'3)Kontroluj biodra, aby cały czas poruszały się w jednej linii.',CHAR(10),'4) Wraz z wydechem wykonaj wyprost w stawach kolanowych i powrót do pozycji wyjściowej.'),CONCAT('1) Nie pochylaj ciała do przodu podczas wykonywania ćwiczenia.',CHAR(10),'2) Pilnuj, aby stopa nogi wykrocznej była dociśnięta do podłogi całą powierzchnią.',CHAR(10),'3) Pamiętaj o utrzymaniu wyprostowanej sylwetki oraz napięciu mięśni brzucha.',CHAR(10),'4) Kontroluj oddech podczas wykonywania ćwiczenia.',CHAR(10),'5) Nie spiesz się podczas wykonywania ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'przysiad_wykroczny_z_noga_zakroczna_na_podwyzszeniu_z_hantlami.mp4',4, '/czworoglowe-uda/Przysiad_wykroczny_z_noga_zakroczna_na_podwyzszeniu_z_hantlami.jpg');
