Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Zakroki z hantlami',CONCAT('1) Pozycja stojąca.',CHAR(10),'2) Hantelki trzymane chwytem młotkowym.',CHAR(10),'3) Klatka piersiowa wypchnięta do przodu, łopatki ściągnięte, brzuch i pośladki napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj zakrok jedną nogą do tyłu. Noga wykroczna pozostaje w miejscu i dociska całą powierzchnią stopy do podłogi. ',CHAR(10),'2) W pozycji wykroczno-zakrocznej ugnij kolana tak, aby uzyskać w obu kąt prosty, kolano nogi zakrocznej nie dotyka podłogi. Nie pochylaj się.',CHAR(10),'3) Wykonując wydech, przenieś ciężar na nogę wykroczną i energiczną pracą nóg wróć do pozycji wyjściowej.',CHAR(10),'4)Wykonaj wyznaczoną liczbę powtórzeń na każdą nogę.'),CONCAT('1) Pamiętaj o prostych plecach oraz odcinku lędźwiowym.',CHAR(10),'2) Kontroluj ruch zakroku.',CHAR(10),'3) Nie bujaj tułowiem, utrzymuj kolce biodrowe na jednym poziomie.',CHAR(10),'4) Głowę trzymaj w równej linii z plecami.',CHAR(10),'5) Wzrok skieruj przed siebie.',CHAR(10),'6) Nie wykonuj kociego grzbietu.',CHAR(10),'7) Bądź skoncentrowany.',CHAR(10),'8) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz).',CHAR(10),'9) Nie spiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'zakroki_z_hantlami.mp4',4, '/czworoglowe-uda/Zakroki_z_hantlami.jpg');
