Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wykroki chodzone z hantlami w dłoniach',CONCAT('1) Pozycja stojąca.',CHAR(10),'2) Hantelki trzymane chwytem młotkowym.',CHAR(10),'3) Klatka piersiowa wypchnięta do przodu, łopatki ściągnięte, brzuch i pośladki napięte.'),CONCAT('1) Z pozycji wyjściowej wykonaj daleki krok w przód. ',CHAR(10),'2) Utrzymując wyprostowaną sylwetkę, ugnij nogi tak, aby kolano z tyłu zatrzymało się tuż nad podłogą, a z przodu nie wystawało za linię palców. ',CHAR(10),'3) Piętę nogi z przodu cały czas mocno dociskaj do podłogi.',CHAR(10),'4) Następnie wykonaj mocny wyprost nogi wykrocznej oraz przeniesienie nogi zakrocznej w przód do kolejnego wykroku. ',CHAR(10),'5) Staraj się nie odkładać nogi zakrocznej do pozycji wyjściowej, lecz kontynuować ruch.'),CONCAT('1) Pilnuj, aby stopa skierowana była w przód lub minimalnie na zewnątrz.',CHAR(10),'2) Pięta nogi wykrocznej cały czas mocno dociśnięta do podłogi. ',CHAR(10),'3) Pamiętaj, aby podczas ruchu utrzymywać wyprostowaną sylwetkę. ',CHAR(10),'4) Utrzymuj biodra na jednej wysokości.',CHAR(10),'5) Nie spiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'wykroki_chodzone_z_hantlami_w_dloniach.mp4',4, '/czworoglowe-uda/Wykroki_chodzone_z_hantlami_w_dloniach.jpg');
