Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przysiad z kettlami',CONCAT('1) Pozycja stojąca, plecy proste.',CHAR(10),'2) Kettle trzymane na wysokości klatki piersiowej, oparte na ramionach, palce splecione.',CHAR(10),'3) Stopy mniej więcej na szerokość barków, ustawione maksymalnie do 15 stopni na zewnątrz.',CHAR(10),'4) Klatka piersiowa wypchnięta do przodu, łopatki ściągnięte, brzuch napięty.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową oraz naturalną krzywiznę kręgosłupa, wykonaj płynny i powolny przysiad do pozycji poniżej kąta prostego wraz z powolnym wdechem powietrza. ',CHAR(10),'2) Wykonując wydech, wstań do pozycji wyjściowej. Nie wykonuj przeprostu w stawach kolanowych. ',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Pamiętaj o prostych plecach i stałym napięciu korpusu.',CHAR(10),'2) Kontroluj ruch przysiadu.',CHAR(10),'3) Trzymaj tułów stabilnie w jednej linii. ',CHAR(10),'4) Głowę trzymaj w równej linii z plecami. ',CHAR(10),'5) Wzrok skieruj przed siebie (ewentualnie lekko do góry). ',CHAR(10),'6) Nie wykonuj kociego grzbietu. ',CHAR(10),'7) Nie wykonuj przeprostu w kolanach. ',CHAR(10),'8) Bądź skoncentrowany. ',CHAR(10),'9) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz). ',CHAR(10),'10) Ciężar rozłóż równomiernie na całych stopach.'),2,'przysiad_z_kettlami.mp4',4, '/czworoglowe-uda/Przysiad_z_kettlami.jpg');
