Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Przysiad ze sztangą trzymaną na plecach',CONCAT('1) Pozycja stojąca ze sztangą na plecach.',CHAR(10),'2) Stopy mniej więcej na szerokości barków (palce stóp na zewnątrz).',CHAR(10),'3) Dłonie na sztandze w szerokim rozstawie.',CHAR(10),'4) Klatka piersiowa wypchnięta do przodu, brzuch i pośladki mocno napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i naturalną krzywiznę kręgosłupa, wykonaj płynny i powolny przysiad do pozycji mniej więcej kąta prostego, wraz z powolnym wdechem powietrza. ',CHAR(10),'2) Wykonując wydech, wstań do pozycji wyjściowej. Nie wykonuj przeprostu w stawach kolanowych. ',CHAR(10),'3) Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Pamiętaj o prostych plecach oraz odcinku lędźwiowym. ',CHAR(10),'2) Kontroluj ruch siadu. ',CHAR(10),'3) Nie bujaj tułowiem (trzymaj stabilnie w jednej linii). ',CHAR(10),'4) Głowa w równej linii z plecami. ',CHAR(10),'5) Wzrok skieruj przed siebie (ew. lekko do góry). ',CHAR(10),'6) Nie wykonuj kociego grzbietu. ',CHAR(10),'7) Nie wykonuj przeprostu w kolanach. ',CHAR(10),'8) Bądź skoncentrowany. ',CHAR(10),'9) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz). ',CHAR(10),'10) Ciężar rozłóż równomiernie na całej stopie.'),2,'video-poprawka-przysiadu.mp4',4, '/czworoglowe-uda/Przysiad_ze_sztanga_trzymana_na_plecach.jpg');
