Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Goblet squat – przysiad z kettlem lub hantelką',CONCAT('1) Pozycja stojąca, plecy proste.',CHAR(10),'2) Kettlebell trzymany oburącz przed klatką piersiową.',CHAR(10),'3) Stopy mniej więcej na szerokości barków (palce stóp na zewnątrz).',CHAR(10),'4) Klatka piersiowa wypchnięta do przodu, łopatki ściągnięte, brzuch i pośladki napięte.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i naturalną krzywiznę kręgosłupa, wykonaj płynny i powolny przysiad do pozycji poniżej kąta prostego, wraz z powolnym wdechem powietrza. ',CHAR(10),'2) Wykonując wydech, wstań do pozycji wyjściowej. Nie rób przeprostu w stawach kolanowych. ',CHAR(10),'3) Powtarzaj ruch wyznaczoną ilość razy.'),CONCAT('1) Pamiętaj o prostych plecach oraz odcinku lędźwiowym. ',CHAR(10),'2) Kontroluj ruch siadu. ',CHAR(10),'3) Nie bujaj tułowiem (trzymaj stabilnie w jednej linii). ',CHAR(10),'4) Głowę trzymaj w równej linii z plecami. ',CHAR(10),'5) Wzrok skieruj przed siebie (ew. lekko do góry). ',CHAR(10),'6) Nie wykonuj kociego grzbietu. ',CHAR(10),'7) Nie wykonuj przeprostu w kolanach. ',CHAR(10),'8) Bądź skoncentrowany. ',CHAR(10),'9) Nie prowadź kolan do wewnątrz (utrzymuj je skierowane lekko na zewnątrz). ',CHAR(10),'10) Ciężar rozłóż równomiernie na całych stopach. ',CHAR(10),'11) Kettlebell oprzyj o klatkę piersiową.'),1,'goblet_squat_przysiad_z_kettlem_lub_hantelka.mp4',4, '/czworoglowe-uda/Goblet_squat_–_przysiad_z_kettlem_lub_hantelka.jpg');
