Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Z-press',CONCAT('1) Pozycja siedząca, nogi rozstawione tak, aby zapewnić stabilną postawę.',CHAR(10),'2) Ramiona w górze, hantle na wysokości głowy. Łokcie skierowane w dół.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, weź wdech i rozpocznij ruch wyciskania ciężaru nad głowę. ',CHAR(10),'2) W końcowej fazie ruchu wykonaj wydech. ',CHAR(10),'3) Następnie powolnym ruchem opuść hantle, wykonując płynny wdech.'),CONCAT('1) Utrzymuj stałe napięcie mięśniowe.',CHAR(10),'2) Łokieć znajduje się pod ciężarem podczas wykonywania ćwiczenia.',CHAR(10),'3) Trzymaj głowę w równej linii z plecami.',CHAR(10),'4) Bądź skoncentrowany.'),1,'video-z-press.mp4',8, '/na-barki/Z-press.jpg');
