Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Face pull z gumą',CONCAT('1) Stań naprzeciw miejsca zaczepienia gumy.',CHAR(10),'2) Stopy ustaw w lekkim rozkroku, plecy proste.',CHAR(10),'3) Złap oburącz za gumę do ćwiczeń, tak aby kciuki wskazywały kierunek na wprost.',CHAR(10),'4) Unieś ramiona przed siebie, łokcie są skierowane na zewnątrz.',CHAR(10),'5) Utrzymuj brzuch w stałym napięciu.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech, a następnie ściśnij łopatki mocno ze sobą oraz opuść je.',CHAR(10),'2) Następnie przyciągnij gumę w kierunku twarzy, kierując łokcie na boki pilnuj, aby barki nie unosiły się.',CHAR(10),'3) Kontynuuj ruch, dopóki łokcie nie przekroczą linii barków, a guma będzie znajdować się przy twarzy.',CHAR(10),'4) Powracaj do pozycji wyjściowej, w której ramiona są wyciągnięte do przodu.'),CONCAT('1) Pamiętaj, aby zawsze ustawić łopatki w prawidłowej pozycji.',CHAR(10),'2) Podczas ruchu nie odchylaj ciała.',CHAR(10),'3) Trzymaj ramiona poziomo, prostopadle do tułowia, a równolegle do podłogiprzez cały ruch.',CHAR(10),'4) Bądź skoncentrowany.',CHAR(10),'5) Dobierz opór gumy tak,aby wykonywać ćwiczenie poprawnie technicznie.',CHAR(10),'6) Nie spiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'video-face-pull-guma.mp4',8, '/na-barki/Face_pull_z_guma.jpg');
