Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Landmine press half kneeling',CONCAT('1) Pozycja klęku wykrocznego.',CHAR(10),'2) Noga wykroczna ugięta pod kątem prostym w biodrze i kolanie.',CHAR(10),'3) Mięśnie brzucha i pośladki mocno napięte, koniec sztangitrzymaj na wysokości barku w ręce przeciwnej do nogi wykrocznej.'),CONCAT('1) Wykonaj mocny wdech, a następnie zdecydowanym ruchem wykonaj wyciśnięcie ciężaru nad głowę. W końcowej fazie ruchu pochyl lekko ciało w przód tak, aby z ramieniem tworzyło jedną linię.',CHAR(10),'2) Utrzymując powietrze, spokojnym ruchem rozpocznij opuszczanie ciężaru. W końcowej fazie ruchu wykonaj wydech, a następnie wdech, przygotowując się do kolejnego powtórzenia.',CHAR(10),'3) Utrzymuj stałe, mocne napięcie, nie bujaj ciałem.'),CONCAT('1) Pamiętaj o utrzymaniu prostych pleców.',CHAR(10),'2) Kontroluj ruch wyciskania i opuszczania.',CHAR(10),'3) Nie bujaj tułowiem (trzymaj go stabilnie w jednej linii).',CHAR(10),'4) Głowę trzymaj w równej linii z plecami.',CHAR(10),'5) Wzrok skieruj przed siebie.',CHAR(10),'6) Bądź skoncentrowany.',CHAR(10),'7) Nie spiesz się podczas wykonywania tego ćwiczenia, zbyt szybkie wykonanie jest błędem technicznym.'),1,'video-landmine-press-half-kneeling.mp4',8, '/na-barki/Landmine_press_half_kneeling.jpg');
