Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie jednorącz nad głowę z gumą',CONCAT('1) Stojąca, plecy wyprostowane. Nogi rozstawione na szerokość bioder, zapewniające stabilną postawę. Guma pod stopą.',CHAR(10),'2) Ramię z gumą ustawione tak, aby dłoń znajdowała się na wysokości brody. Łokieć ugięty znajduje się w jednej linii z nadgarstkiem.',CHAR(10),'3) Druga ręka wyprostowana wzdłuż tułowia.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową wykonaj wdech i wyciśnij ramię nad głowę, prostując ramię w łokciu.',CHAR(10),'2) W końcowej fazie ruchu wykonaj wydech. Następnie powolnym ruchem opuszczaj ramię, wykonując również płynny wdech.',CHAR(10),'3)Wykonaj wyznaczoną liczbę powtórzeń, po czym zmień stronę.'),CONCAT('1) Utrzymuj stałe napięcie mięśniowe.',CHAR(10),'2) Dobierz odpowiedni opór gumy.',CHAR(10),'3) Trzymaj głowę w równej linii z plecami.',CHAR(10),'4) Napinając pośladki i uruchamiając tłocznię brzuszną, dbajo prawidłową postawę ciała.',CHAR(10),'5) Nie wyginaj pleców w łuk.',CHAR(10),'6) Bądź skoncentrowany.'),1,'video-wyciskanie-jednoracz-nad-glowe-z-guma.mp4',8, '/na-barki/Wyciskanie_jednoracz_nad_glowe_z_guma.jpg');
