Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie ramion z gumą nad głowę',CONCAT('1) Złap gumę nachwytem zamkniętym nieco szerzej niż na szerokość barków.',CHAR(10),'2) Stopy w lekkim rozkroku. Plecy proste, brzuch napięty.',CHAR(10),'3) Gumę utrzymuj na wysokości obojczyków, drugi koniec pod stopami.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, wykonaj wdech i rozpocznij wyciskanie ramion z gumą w górę. Łokcie prowadź przodem, przedramiona staraj się utrzymać na stałe skierowane w górę.',CHAR(10),'2) Po wyciśnięciu wykonaj wydech, nie blokuj rąk w stawach łokciowych – nie dopuszczaj do ich przeprostu.',CHAR(10),'3) Następnie wykonaj wdech i opuść ramiona.',CHAR(10),'4) Wróć do pozycji wyjściowej, opuszczając gumę do linii obojczyków.',CHAR(10),'5) Wykonajwyznaczoną liczbę powtórzeń.'),CONCAT('1) Utrzymaj stałe napięcie ćwiczonych mięśni.',CHAR(10),'2) Ustabilizuj sylwetkę, napinając mięśnie brzucha i pośladki.',CHAR(10),'3) Dobierz opór gumy tak, aby pozwolił Ci na wykonanie ćwiczenia w pełni poprawnie.',CHAR(10),'4) Bądź skoncentrowany.'),1,'video-wyciskanie-ramion-z-guma-nad-glowe.mp4',8, '/na-barki/Wyciskanie_ramion_z_guma_nad_glowe.jpg');
