Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Unoszenie ramion z hantlami w przód z przenoszeniem na boki',CONCAT('1) Pozycja stojąca, ramiona wyprostowane wzdłuż tułowia, dłonie trzymają hantle.',CHAR(10),'2) Stopy w lekkim rozkroku. Plecy proste.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową i unikając kołysania tułowiem, zbliż łopatki, wykonaj wdech i unieś hantle w przód, aż ramiona będą ustawione równolegle do podłogi.',CHAR(10),'2) Następnie utrzymując hantle na wysokości barków, przenieś ramiona w bok i z powrotem w przód. ',CHAR(10),'3) Powolnym ruchem opuść hantle do pozycji wyjściowej.'),CONCAT('1) Utrzymuj stałe napięcie mięśni.',CHAR(10),'2) Nie kołysz tułowiem. Pamiętaj o właściwym doborze ciężaru.',CHAR(10),'3) Trzymaj głowę w równej linii z plecami.',CHAR(10),'4) Bądź skoncentrowany.'),1,'unoszenie_ramion_w_przod_z przenoszeniem_na_boki.mp4',8, '/na-barki/Unoszenie_ramion_z_hantlami_w_przod_z_przenoszeniem_na_boki.jpg');
