Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Cuban press',CONCAT('1)Pozycja stojąca, ramiona luźno opuszczone, hantle w dłoniach.'),CONCAT('1) Weź wdech, wykonaj wznos ramion bokiem na wysokości barków, przy czym utrzymuj łokcie zgięte pod kątem prostym.',CHAR(10),'2) Nie zmieniając pozycji łokci, zacznij powoli unosić hantle poprzez rotację w stawie barkowym. ',CHAR(10),'3) Gdy przedramiona będą skierowane w górę, wykonaj wyciśnięcie hantli nad głowę, wypuszczając powietrze. ',CHAR(10),'4) Weź wdech i powolnym, kontrolowanym ruchem opuść hantle takim samym torem ruchu jak przy podnoszeniu.'),CONCAT('1) Ćwiczenie wymaga precyzji technicznej oraz odpowiedniej liczby powtórzeń. Nigdy nie przedkładaj ciężaru nad poprawność techniczną.',CHAR(10),'2) Przez cały ruch postaraj się utrzymać złączone łopatki.',CHAR(10),'3) Nie unoś barków.',CHAR(10),'4) Nie wypychaj bioder. ',CHAR(10),'5) Nadgarstki mają stanowić jedną linię z przedramionami przez cały czas ćwiczenia. ',CHAR(10),'6) Zbyt szybkie wykonanie ćwiczenia jest błędem technicznym.'),1,'cuban_press.mp4',8, '/na-barki/Cuban_press.jpg');
