Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie sztangi zza głowy stojąc',CONCAT('1) Chwyć sztangę nachwytem (palce wskazują przód) nieco szerzej niż szerokość barków. ',CHAR(10),'2) Stań prosto. Zarzuć sztangę na barki. ',CHAR(10),'3) Stopy w lekkim rozkroku.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, unieś sztangę nad głowę, prostując ramiona w łokciach i wykonaj wydech. ',CHAR(10),'2) Opuszczaj powoli sztangę za głowę, staraj się nie przekraczać linii uszu w początkowych próbach ćwiczenia – tak aby uniknąć kontuzji. ',CHAR(10),'3) Wyciskaj sztangę nad głowę, wykonując wydech powietrza. ',CHAR(10),'4) Powtarzaj ruch zadaną ilość razy.'),CONCAT('1) Stopy mocno wciśnij w podłogę. ',CHAR(10),'2) Trzymaj głowę w równej linii z plecami. ',CHAR(10),'3) Bądź skoncentrowany.'),1,'wyciskanie_sztangi_zza_glowy_stojac.mp4',8, '/na-barki/Wyciskanie_sztangi_zza_glowy_stojac.jpg');
