Insert into exercises (name,description_of_start_position,description_of_correct_execution, hints, exercise_form_id, video_url, body_part_id, photo_url) VALUES ('Wyciskanie sztangi nad głowę siedząc',CONCAT('1) Siedząca na ławce płaskiej, plecy na oparciu. ',CHAR(10),'2) Stopy w lekkim rozkroku, mocno zaparte o podłoże. ',CHAR(10),'3) Chwyć sztangę nachwytem (palce wskazują przód) nieco szerzej niż szerokość barków.'),CONCAT('1) Utrzymując prawidłową pozycję wyjściową, unieś sztangę nad głowę, prostując ramiona w łokciach i wykonaj wydech. ',CHAR(10),'2) Następnie powolnym ruchem opuszczaj sztangę, uginając ramiona w łokciach, do klatki piersiowej i wykonuj płynny wdech. ',CHAR(10),'3)Wykonaj wyznaczoną liczbę powtórzeń.'),CONCAT('1) Stopy mocno wciśnij w podłogę. ',CHAR(10),'2) Trzymaj głowę w równej linii z plecami. ',CHAR(10),'3) Bądź skoncentrowany.'),1,'wyciskanie_sztangi_nad_glowe (siedzac).mp4',8, '/na-barki/Wyciskanie_sztangi_nad_glowe_siedzac.jpg');
